import React, { useEffect, useState } from "react";
import CatCard from "./components/CatCard";
import Summary from "./components/Summary";

function App() {
  const [cats, setCats] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [likedCats, setLikedCats] = useState([]);
  const [isFinished, setIsFinished] = useState(false);

  // Fetch 10 random cats from Cataas
  const fetchCats = async () => {
    const catPromises = Array.from({ length: 10 }, async () => {
      const res = await fetch("https://cataas.com/cat?json=true");
      const data = await res.json();
      return `https://cataas.com${data.url}`;
    });
    const catImages = await Promise.all(catPromises);
    setCats(catImages);
    setCurrentIndex(0);
    setLikedCats([]);
    setIsFinished(false);
  };

  useEffect(() => {
    fetchCats();
  }, []);

  const handleSwipe = (direction) => {
    if (direction === "right") {
      setLikedCats([...likedCats, cats[currentIndex]]);
    }

    if (currentIndex + 1 === cats.length) {
      setIsFinished(true);
    } else {
      setCurrentIndex(currentIndex + 1);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-4">
      <h1 className="text-3xl font-bold mb-6">Paws & Preferences</h1>

      {!isFinished && cats.length > 0 && (
        <CatCard
          key={currentIndex}
          image={cats[currentIndex]}
          onSwipe={handleSwipe}
        />
      )}

      {isFinished && (
        <Summary likedCats={likedCats} onRestart={fetchCats} />
      )}
    </div>
  );
}

export default App;
