import React, { useState } from "react";
import TinderCard from "react-tinder-card";
import { motion, AnimatePresence } from "framer-motion";

const CatCard = ({ image, onSwipe }) => {
  const [swipeDir, setSwipeDir] = useState(null);

  const handleSwipe = (dir) => {
    setSwipeDir(dir);
    setTimeout(() => {
      setSwipeDir(null);
    }, 300);
    onSwipe(dir);
  };

  return (
    <AnimatePresence>
      <TinderCard
        onSwipe={handleSwipe}
        preventSwipe={["up", "down"]}
      >
        <motion.div
          className="w-80 h-96 rounded-xl shadow-lg overflow-hidden bg-white relative"
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          exit={{ x: swipeDir === "right" ? 500 : -500, opacity: 0 }}
          transition={{ duration: 0.3 }}
        >
          <img
            src={image}
            alt="Cat"
            className="w-full h-full object-cover"
          />
          {swipeDir === "right" && (
            <div className="absolute top-0 left-0 w-full h-full bg-green-500/30 flex items-center justify-center text-white font-bold text-3xl">
              LIKE
            </div>
          )}
          {swipeDir === "left" && (
            <div className="absolute top-0 left-0 w-full h-full bg-red-500/30 flex items-center justify-center text-white font-bold text-3xl">
              NOPE
            </div>
          )}
        </motion.div>
      </TinderCard>
    </AnimatePresence>
  );
};

export default CatCard;
